package company.gg;

import static org.junit.Assert.assertFalse;
import static org.junit.Assert.assertTrue;

public class RobotReturnToOrigin_657 {

    // The JVM is caching Integer values. == only works for numbers between -128 and 127
    // http://www.owasp.org/index.php/Java_gotchas#Immutable_Objects_.2F_Wrapper_Class_Caching
    public static boolean judgeCircle(String moves) {
        int x = 0, y = 0;
        for (char c : moves.toCharArray()) {
            if ('L' == c) x--;
            else if ('R' == c) x++;
            else if ('U' == c) y++;
            else if ('D' == c) y--;
        }
        return x == 0 && y == 0;
    }

    public static void main(String[] args) {
        assertTrue(judgeCircle(""));
        assertTrue(judgeCircle("UD"));
        assertTrue(judgeCircle("ULDR"));
        assertTrue(judgeCircle("ULULDRDR"));
        assertFalse(judgeCircle("U"));
        assertFalse(judgeCircle("UUDLR"));
        assertTrue(judgeCircle(""));
    }
}
